import App from './App.js';
import Footer from './footer'

class Imprint extends React.Component{
    render(){
        return (
                <Container style={{padding: 50 + "px"}}>
                    <Row>
                        <Col>
                            <p><Link to="/" element={<App />}>Zurück zur Home Seite</Link></p>
